// Extract the data from the previous node
const invoiceData = items[0].json.Invoice;

// Initialize the result object with the necessary fields
const result = {
  ParentDocumentID: invoiceData["cbc:ID"] || "",
  IssueDate: invoiceData["cbc:IssueDate"] || "",

  // Sender Party details
  SenderParty_RegistrationName: invoiceData["cac:AccountingSupplierParty"]?.["cac:Party"]?.["cac:PartyTaxScheme"]?.["cbc:RegistrationName"] || "",
  SenderParty_CompanyID: invoiceData["cac:AccountingSupplierParty"]?.["cac:Party"]?.["cac:PartyTaxScheme"]?.["cbc:CompanyID"]?._ || "",
  SenderParty_CityID: invoiceData["cac:AccountingSupplierParty"]?.["cac:Party"]?.["cac:PhysicalLocation"]?.["cac:Address"]?.["cbc:ID"] || "",
  SenderParty_CityName: invoiceData["cac:AccountingSupplierParty"]?.["cac:Party"]?.["cac:PhysicalLocation"]?.["cac:Address"]?.["cbc:CityName"] || "",
  SenderParty_TaxLevelCode: invoiceData["cac:AccountingSupplierParty"]?.["cac:Party"]?.["cac:PartyTaxScheme"]?.["cbc:TaxLevelCode"]?._ || "",
  SenderParty_IndustryClassificationCode: invoiceData["cac:AccountingSupplierParty"]?.["cac:Party"]?.["cbc:IndustryClassificationCode"] || "",
  SenderParty_CompanyType: invoiceData["cac:AccountingSupplierParty"]?.["cbc:AdditionalAccountID"] || "",

  // Receiver Party details
  ReceiverParty_RegistrationName: invoiceData["cac:AccountingCustomerParty"]?.["cac:Party"]?.["cac:PartyTaxScheme"]?.["cbc:RegistrationName"] || "",
  ReceiverParty_CompanyID: invoiceData["cac:AccountingCustomerParty"]?.["cac:Party"]?.["cac:PartyTaxScheme"]?.["cbc:CompanyID"]?._ || "",
  ReceiverParty_CityID: invoiceData["cac:AccountingCustomerParty"]?.["cac:Party"]?.["cac:PhysicalLocation"]?.["cac:Address"]?.["cbc:ID"] || "",
  ReceiverParty_CityName: invoiceData["cac:AccountingCustomerParty"]?.["cac:Party"]?.["cac:PhysicalLocation"]?.["cac:Address"]?.["cbc:CityName"] || "",
  ReceiverParty_TaxLevelCode: invoiceData["cac:AccountingCustomerParty"]?.["cac:Party"]?.["cac:PartyTaxScheme"]?.["cbc:TaxLevelCode"]?._ || "",


  // Other existing fields
  LineExtensionAmount: invoiceData["cac:LegalMonetaryTotal"]?.["cbc:LineExtensionAmount"]?._ || "",
  TaxInclusiveAmount: invoiceData["cac:LegalMonetaryTotal"]?.["cbc:TaxInclusiveAmount"]?._ || "",

  IVA: "",
  IC: "",
  ICA: "",
  INC: "",
  ReteIVA: "",
  ReteFuente: "",
  ReteICA: "",

  PayableAmount: invoiceData["cac:LegalMonetaryTotal"]?.["cbc:PayableAmount"]?._ || "",

  LineCountNumeric: invoiceData["cbc:LineCountNumeric"] || "",

  InvoiceLine: "",
  CUFE: invoiceData["cbc:UUID"]?._ || "",

  // New variables for InvoiceTypeCode and DocumentCurrencyCode
  InvoiceTypeCode: invoiceData["cbc:InvoiceTypeCode"] || "",
  DocumentCurrencyCode: invoiceData["cbc:DocumentCurrencyCode"] || "",

  // Eventos de un Documento Electrónico: cbc:DocumentResponse/Description; cbc:ResponseCode
  DocumentResponseDescription: invoiceData["cac:DocumentResponse"]?.["cbc:Description"] || "",
  DocumentResponseCode: invoiceData["cac:DocumentResponse"]?.["cbc:ResponseCode"] || ""
};

// Extract tax information
const taxDetails = invoiceData["cac:TaxTotal"]?.["cac:TaxSubtotal"];
if (Array.isArray(taxDetails)) {
  // Iterate through the array to find IVA
  result.IVA = taxDetails
    .filter(subtotal => subtotal["cac:TaxCategory"]?.["cac:TaxScheme"]?.["cbc:ID"] === "01")
    .map(subtotal => parseFloat(subtotal["cbc:TaxAmount"]?._ || "0"))
    .reduce((acc, amount) => acc + amount, 0)
    .toFixed(2); // Sum all IVA amounts
} else if (taxDetails?.["cac:TaxCategory"]?.["cac:TaxScheme"]?.["cbc:ID"] === "01") {
  result.IVA = taxDetails["cbc:TaxAmount"]?._ || "";
}

// Extract withholding taxes
const withholdingTaxes = invoiceData["cac:WithholdingTaxTotal"];
if (Array.isArray(withholdingTaxes)) {
  withholdingTaxes.forEach((tax) => {
    const taxSchemeID = tax["cac:TaxSubtotal"]?.["cac:TaxCategory"]?.["cac:TaxScheme"]?.["cbc:ID"];
    const taxAmount = parseFloat(tax["cac:TaxSubtotal"]?.["cbc:TaxAmount"]?._ || "0");
    switch (taxSchemeID) {
      case "05":
        result.ReteIVA = (parseFloat(result.ReteIVA) || 0) + taxAmount;
        break;
      case "06":
        result.ReteFuente = (parseFloat(result.ReteFuente) || 0) + taxAmount;
        break;
      case "07":
        result.ReteICA = (parseFloat(result.ReteICA) || 0) + taxAmount;
        break;
    }
  });

  // Convert to strings to avoid potential type mismatch
  result.ReteIVA = result.ReteIVA ? result.ReteIVA.toFixed(2) : "";
  result.ReteFuente = result.ReteFuente ? result.ReteFuente.toFixed(2) : "";
  result.ReteICA = result.ReteICA ? result.ReteICA.toFixed(2) : "";
}

// Extract invoice line items
const invoiceLines = invoiceData["cac:InvoiceLine"];
let invoiceLineDescriptions = [];

if (Array.isArray(invoiceLines)) {
  // If it's an array, loop through each invoice line
  invoiceLines.forEach(invoiceLine => {
    const description = invoiceLine["cac:Item"]?.["cbc:Description"] || "";
    const quantity = invoiceLine["cbc:InvoicedQuantity"]?._ || "";
    const unitValue = invoiceLine["cac:Price"]?.["cbc:PriceAmount"]?._ || "";
    const totalValue = invoiceLine["cbc:LineExtensionAmount"]?._ || "";

    invoiceLineDescriptions.push(`Description: ${description}, Quantity: ${quantity}, Unit Value: ${unitValue}, Total Value: ${totalValue}`);
  });
} else if (invoiceLines) {
  // If it's a single object
  const description = invoiceLines["cac:Item"]?.["cbc:Description"] || "";
  const quantity = invoiceLines["cbc:InvoicedQuantity"]?._ || "";
  const unitValue = invoiceLines["cac:Price"]?.["cbc:PriceAmount"]?._ || "";
  const totalValue = invoiceLines["cbc:LineExtensionAmount"]?._ || "";

  invoiceLineDescriptions.push(`Description: ${description}, Quantity: ${quantity}, Unit Value: ${unitValue}, Total Value: ${totalValue}`);
}

// Combine all invoice lines into a single string, separated by semicolons
result.InvoiceLine = invoiceLineDescriptions.join("; ");

// Return the result as JSON
return [{ json: result }];

// Access the 'Invoice' data from the XML to JSON node
const invoiceData = items[0].json.Invoice;

// Extract invoice line items from the XML data
const invoiceLines = invoiceData["cac:InvoiceLine"];
let result = {};  // Initialize the result object

// Check if 'InvoiceLine' is an array and process each item
if (Array.isArray(invoiceLines)) {
  invoiceLines.forEach((invoiceLine, index) => {
    const description = invoiceLine["cac:Item"]?.["cbc:Description"] || "";
    const quantity = invoiceLine["cbc:InvoicedQuantity"]?._ || "";
    const unitValue = invoiceLine["cac:Price"]?.["cbc:PriceAmount"]?._ || "";
    const totalValue = invoiceLine["cbc:LineExtensionAmount"]?._ || "";

    // Create a key for each item (item_1, item_2, etc.)
    result[`item_${index + 1}`] = {
      "ItemDescription": description,
      "Quantity": quantity,
      "Unit_Value": unitValue,
      "Total_Value": totalValue
    };
  });
} else if (invoiceLines) {
  // If there's only one item (not an array)
  const description = invoiceLines["cac:Item"]?.["cbc:Description"] || "";
  const quantity = invoiceLines["cbc:InvoicedQuantity"]?._ || "";
  const unitValue = invoiceLines["cac:Price"]?.["cbc:PriceAmount"]?._ || "";
  const totalValue = invoiceLines["cbc:LineExtensionAmount"]?._ || "";

  // Handle the single item case
  result["item_1"] = {
    "ItemDescription": description,
    "Quantity": quantity,
    "Unit_Value": unitValue,
    "Total_Value": totalValue
  };
}

// Return the result as the output
return [{ json: result }];

// Function to remove null values from an object
const removeNulls = (obj) => {
  for (const key in obj) {
    if (obj[key] === null) {
      delete obj[key];
    }
  }
  return obj;
};

// Apply the function to each item in the 'items' array provided by n8n
return items.map(item => {
  const cleanedData = removeNulls(item.json);
  return {
    json: cleanedData
  };
});

// Iterate over all items in the input
return items.map(item => {
    const jsonOutput = item.json; // This will get the input JSON for the current item

    const invoiceData = jsonOutput.InvoiceData || {};
    const invoiceDescription = jsonOutput.Invoice_Description || {};

    // Calculate IVA with the provided conditions
    let iva = parseFloat(invoiceData.IVA) || 0;
    const taxInclusiveAmount = parseFloat(invoiceData.TaxInclusiveAmount) || 0;
    const lineExtensionAmount = parseFloat(invoiceData.LineExtensionAmount) || 0;

    // Condition 1: Set IVA if it’s zero and there’s a difference between TaxInclusiveAmount and LineExtensionAmount
    if (iva === 0 && (taxInclusiveAmount - lineExtensionAmount) > 0) {
        iva = taxInclusiveAmount - lineExtensionAmount;
    }

    // Condition 2: Restrict IVA value
    if (iva < 0) {
        iva = "Error"; // Set IVA to "Error" if it exceeds the limits
    }

    // Prepare the object for Supabase insertion
    const preparedData = {
        ParentDocumentID: invoiceData.ParentDocumentID || null,
        IssueDate: invoiceData.IssueDate || null,
        SenderParty_RegistrationName: invoiceData.SenderParty_RegistrationName || null,
        SenderParty_CompanyID: parseFloat(invoiceData.SenderParty_CompanyID) || null,
        SenderParty_CityID: invoiceData.SenderParty_CityID || null,
        SenderParty_CityName: invoiceData.SenderParty_CityName || null,
        // Concatenate the value from invoiceData and Clean JSON
        SenderParty_TaxLevelCode: (invoiceData.SenderParty_TaxLevelCode ? invoiceData.SenderParty_TaxLevelCode + ', ' + $('Clean JSON').item.json.SenderParty_TaxLevelCode : $('Clean JSON').item.json.SenderParty_TaxLevelCode || null),
        SenderParty_IndustryClassificationCode: invoiceData.SenderParty_IndustryClassificationCode || null,
        ReceiverParty_RegistrationName: invoiceData.ReceiverParty_RegistrationName || null,
        ReceiverParty_CompanyID: parseFloat(invoiceData.ReceiverParty_CompanyID) || null,
        ReceiverParty_CityID: invoiceData.ReceiverParty_CityID || null,
        ReceiverParty_CityName: invoiceData.ReceiverParty_CityName || null,
        ReceiverParty_TaxLevelCode: invoiceData.ReceiverParty_TaxLevelCode || null,
        LineExtensionAmount: lineExtensionAmount || null,
        TaxInclusiveAmount: taxInclusiveAmount || null,
        IVA: iva, // Set the computed IVA value here
        IC: parseFloat(invoiceData.IC) || null,
        ICA: parseFloat(invoiceData.ICA) || null,
        INC: parseFloat(invoiceData.INC) || null,
        ReteIVA: parseFloat(invoiceData.ReteIVA) || null,
        ReteFuente: parseFloat(invoiceData.ReteFuente) || null,
        ReteICA: parseFloat(invoiceData.ReteICA) || null,
        PayableAmount: parseFloat(invoiceData.PayableAmount) || null,
        LineCountNumeric: parseInt(invoiceData.LineCountNumeric, 10) || null,
        InvoiceLine: invoiceData.InvoiceLine || null,
        CUFE: invoiceData.CUFE || null,
        InvoiceTypeCode: invoiceData.InvoiceTypeCode || null,
        DocumentCurrencyCode: invoiceData.DocumentCurrencyCode || null,
        DocumentResponseDescription: invoiceData.DocumentResponseDescription || null,
        DocumentResponseCode: invoiceData.DocumentResponseCode || null,
        InvoiceDescription: invoiceDescription.Description || null,
        ExpenseType: invoiceDescription.ExpenseType || null,
        DataExtractionDate: jsonOutput.DataExtractionDate || null,
        FileName: jsonOutput.FileName || null,
        FileFormat: jsonOutput.FileFormat || null,
        AI_Model: jsonOutput.AI_Model || null,
        TokenIn: parseInt(jsonOutput.TokenIn, 10) || null,
        TokenOut: parseInt(jsonOutput.TokenOut, 10) || null
    };

    return {
        json: preparedData // Output the prepared data in the correct format for the next node
    };
});

// Retrieve aggregated data from Aggregate_DataItem node
const aggregateData = $('Aggregate_DataItem').item.json;

// Extract the common fields (these will be the same for all items)
const parentDocumentID = aggregateData.ParentDocumentID[0];
const senderPartyCompanyID = aggregateData.SenderParty_CompanyID[0];
const senderPartyRegistrationName = aggregateData.SenderParty_RegistrationName[0];
const senderPartyTaxLevelCode = aggregateData.SenderParty_TaxLevelCode[0];
const Invoice_uuid = aggregateData.Invoice_uuid[0]

// Extract the items from the content field
const contentItems = aggregateData.content[0];

// Prepare an array to hold the individual items
const individualItems = [];

// Iterate over the items in the content object and convert them to individual items
Object.keys(contentItems).forEach(itemKey => {
  const itemData = contentItems[itemKey];

  // Create the final object for each individual item
  const finalItem = {
    ParentDocumentID: parentDocumentID,
    SenderParty_CompanyID: senderPartyCompanyID,
    SenderParty_RegistrationName: senderPartyRegistrationName,
    SenderParty_TaxLevelCode: senderPartyTaxLevelCode,
    ItemDescription: itemData.ItemDescription,
    Quantity: itemData.Quantity,
    Unit_Value: itemData.Unit_Value,
    Total_Value: itemData.Total_Value,
    Invoice_uuid: Invoice_uuid
  };

  // Add the final item to the array
  individualItems.push({ json: finalItem });
});

// Return the individual items
return individualItems;

// Get the current month and year
const currentDate = new Date();
const currentYear = currentDate.getFullYear();
const currentMonth = currentDate.getMonth() + 1; // getMonth() is 0-indexed

// Access IssueDate from the nested structure in "Invoice Data" node
const issueDateString = $json.choices?.[0]?.message?.content?.IssueDate || ""; // Ensure IssueDate exists
if (!issueDateString) {
    return [{ json: { result: "Error: IssueDate is missing" }}];
}

// Parse IssueDate
const [issueYear, issueMonth] = issueDateString.split('-').map(Number);

// Check if the IssueDate month and year match the current month and year
let result;
if (issueYear === currentYear && issueMonth === currentMonth) {
    result = "Mes Ok";
} else {
    result = "Error Mes";
}

return [{ json: { result } }];

// Access the ParentDocumentID value
const parentDocumentID = $json.ParentDocumentID?.[0] || null; // Get the first element of the array or null if undefined

// Check if ParentDocumentID is neither null, 0, nor "0:0"
let result;
if (parentDocumentID && parentDocumentID !== "0" && parentDocumentID !== "0:0") {
    result = "Ok";
} else {
    result = "Error";
}

return [{ json: { result } }];
