// 🔹 Función para validar campos obligatorios
const validarCampo = (valor, campo) => {
    if (!valor || valor === "") {
        console.warn(`⚠️ Advertencia: ${campo} está vacío o ausente.`);
        return "SIN_" + campo.toUpperCase(); // Se asigna un valor de respaldo
    }
    return valor;
};

// 🔹 Obtener datos de los nodos
const invoiceData = $('Invoice_Data').item.json || {};
const allInvoiceData = $('All_Invoice_Data').item.json || {};
const aggregateInvoiceData = $('Aggregate_Invoice_Data').item.json || {};

// 🔹 Extraer InvoiceDescription con prioridad de nodos
const invoiceDescription = invoiceData.InvoiceDescription || 
                           allInvoiceData.InvoiceDescription || 
                           aggregateInvoiceData.InvoiceDescription || 
                           "SIN_DESCRIPCIÓN";

// 🔹 Función para convertir valores a números seguros
const convertirNumero = (valor) => {
    const num = parseFloat(valor);
    return isNaN(num) ? null : num;
};

// 🔹 Procesar cada factura
const allProcessedData = items.map(item => {
    const classification = item.json.Account_Classification;

    return {
        // 📌 **Datos clave de la factura**
        Invoice_uuid: validarCampo(allInvoiceData.Invoice_uuid, "Invoice_uuid"),
        ParentDocumentID: validarCampo(invoiceData.ParentDocumentID, "ParentDocumentID"),
        SenderParty_CompanyID: validarCampo(invoiceData.SenderParty_CompanyID, "SenderParty_CompanyID"),
        ReceiverParty_CompanyID: validarCampo(invoiceData.ReceiverParty_CompanyID, "ReceiverParty_CompanyID"),
        PayableAmount: convertirNumero(invoiceData.PayableAmount),
        InvoiceDescription: validarCampo(invoiceDescription, "InvoiceDescription"),
        ExpenseType: validarCampo(invoiceData.ExpenseType, "ExpenseType"),

        // 📌 **Cuentas contables**
        AccountNum1: classification.AccountNum1 || null,
        AccountNum1_Des: classification.AccountNum1_Des || null,
        AccountNum1_Val: convertirNumero(classification.AccountNum1_Val),
        AccountNum1_Deb_Cred: classification.AccountNum1_Deb_Cred || null,

        AccountNum2: classification.AccountNum2 || null,
        AccountNum2_Des: classification.AccountNum2_Des || null,
        AccountNum2_Val: convertirNumero(classification.AccountNum2_Val),
        AccountNum2_Deb_Cred: classification.AccountNum2_Deb_Cred || null,

        AccountNum3: classification.AccountNum3 || null,
        AccountNum3_Des: classification.AccountNum3_Des || null,
        AccountNum3_Val: convertirNumero(classification.AccountNum3_Val),
        AccountNum3_Deb_Cred: classification.AccountNum3_Deb_Cred || null,

        AccountNum4: classification.AccountNum4 || null,
        AccountNum4_Des: classification.AccountNum4_Des || null,
        AccountNum4_Val: convertirNumero(classification.AccountNum4_Val),
        AccountNum4_Deb_Cred: classification.AccountNum4_Deb_Cred || null,

        AccountNum5: classification.AccountNum5 || null,
        AccountNum5_Des: classification.AccountNum5_Des || null,
        AccountNum5_Val: convertirNumero(classification.AccountNum5_Val),
        AccountNum5_Deb_Cred: classification.AccountNum5_Deb_Cred || null,

        AccountNum6: classification.AccountNum6 || null,
        AccountNum6_Des: classification.AccountNum6_Des || null,
        AccountNum6_Val: convertirNumero(classification.AccountNum6_Val),
        AccountNum6_Deb_Cred: classification.AccountNum6_Deb_Cred || null,

        AccountNum7: classification.AccountNum7 || null,
        AccountNum7_Des: classification.AccountNum7_Des || null,
        AccountNum7_Val: convertirNumero(classification.AccountNum7_Val),
        AccountNum7_Deb_Cred: classification.AccountNum7_Deb_Cred || null,

        AccountNum8: classification.AccountNum8 || null,
        AccountNum8_Des: classification.AccountNum8_Des || null,
        AccountNum8_Val: convertirNumero(classification.AccountNum8_Val),
        AccountNum8_Deb_Cred: classification.AccountNum8_Deb_Cred || null,

        // 📌 **Datos adicionales**
        Confidence: convertirNumero(classification.Confidence),
        TokenIn: convertirNumero(invoiceData.TokenIn),
        TokenOut: convertirNumero(invoiceData.TokenOut),
        AI_Model: invoiceData.AI_Model || null
    };
});

// 🔹 Retornar los datos corregidos
return allProcessedData;

/**
 * Clean Vendor JSON – Code node
 * Toma la salida cruda de Supabase (Account_Number_1…Account_Number_40,
 * Account_to_InvoiceData_1…Account_to_InvoiceData_40, Account_Description_1…Account_Description_40)
 * y construye un array `mappings` solo con las cuentas realmente parametrizadas.
 */
return items.map(item => {
  const raw = item.json;
  const mappings = [];

  // Recorre los 40 posibles campos de cuenta
  for (let i = 1; i <= 40; i++) {
    const num = raw[`Account_Number_${i}`];
    const to  = raw[`Account_to_InvoiceData_${i}`];
    const des = raw[`Account_Description_${i}`];

    // Solo agrega mappings válidos (número y destino no vacíos)
    if (num && num.toString().trim() !== '' && to && to.toString().trim() !== '') {
      mappings.push({
        invoiceField: to.toString().trim(),
        number:        num.toString().trim(),
        description:   des ? des.toString().trim() : ''
      });
    }
  }

  // Devuelve solo el array de mappings
  return {
    json: { mappings }
  };
});

