// Obtener los valores desde "All_Invoice_Data" (Base de Accounting Records)
const invoiceData = $('All_Invoice_Data').item.json || {};

// Verificación de Valores Contables
const supabaseValues = [
    invoiceData.LineExtensionAmount || 0,
    invoiceData.IVA || 0,
    invoiceData.IC || 0,
    invoiceData.ICA || 0,
    invoiceData.INC || 0,
    invoiceData.ReteIVA || 0,
    invoiceData.ReteFuente || 0,
    invoiceData.ReteICA || 0
];

// Obtener valores de "Dat for DB" excluyendo claves no relacionadas con montos
const datForDb = $('Dat for DB').item.json || {};
const datForDbValues = [
    datForDb.AccountNum1_Val || 0,
    datForDb.AccountNum2_Val || 0,
    datForDb.AccountNum3_Val || 0,
    datForDb.AccountNum4_Val || 0,
    datForDb.AccountNum5_Val || 0,
    datForDb.AccountNum6_Val || 0,
    datForDb.AccountNum7_Val || 0,
    datForDb.AccountNum8_Val || 0
];

// Filtrar valores nulos o cero
const filteredSupabaseValues = supabaseValues.filter(value => value !== null && value !== 0);
const filteredDatForDbValues = datForDbValues.filter(value => value !== null && value !== 0);

// Verificación Mejorada de Valores
const allValuesFound = filteredSupabaseValues.every(value => 
    filteredDatForDbValues.some(dbValue => Math.abs(dbValue - value) < 0.01)
);

// Validación de Cuentas Contables (Obligatorias)
let requiredAccounts = ["AccountNum1", "AccountNum8"];

// Si hay IVA, la cuenta de IVA debe existir
if (invoiceData.IVA > 0 && (!datForDb.AccountNum2 || datForDb.AccountNum2 === "00000000")) {
    requiredAccounts.push("AccountNum2");
}

const missingAccounts = requiredAccounts.filter(acc => !datForDb[acc] || datForDb[acc] === "00000000");

// Preparar mensajes de error
const errorMessages = [];
if (!allValuesFound) errorMessages.push("Descuadre entre valores económicos y cuentas contables.");
if (missingAccounts.length > 0 && missingAccounts !== "Ninguna") errorMessages.push("Cuentas contables faltantes: " + missingAccounts.join(", "));

// Determinar resultado
const isOk = allValuesFound && missingAccounts.length === 0;

// Generar Error_log si hay error (para logging estructurado)
let errorLog = null;
if (!isOk) {
    errorLog = {
        Error_uuid: (typeof crypto !== 'undefined' && crypto.randomUUID) ? crypto.randomUUID() : (datForDb.Invoice_uuid || invoiceData.Invoice_uuid || "SIN_UUID"),
        ParentDocumentID: invoiceData.ParentDocumentID || "SIN_DOCUMENTO",
        IssueDate: invoiceData.IssueDate || "0000-00-00",
        SenderParty_RegistrationName: invoiceData.SenderParty_RegistrationName || "SIN_NOMBRE",
        SenderParty_CompanyID: invoiceData.SenderParty_CompanyID || "SIN_ID",
        ReceiverParty_RegistrationName: invoiceData.ReceiverParty_RegistrationName || "SIN_NOMBRE",
        ReceiverParty_CompanyID: invoiceData.ReceiverParty_CompanyID || "SIN_ID",
        FileName: invoiceData.FileName || null,
        CUFE: invoiceData.CUFE || null,
        "Accounting_Records – Módulo": "Asignación Cuentas",
        Error_Log: errorMessages.join("; "),
        Error_Date: (new Date()).toISOString()
    };
}

// Resultado Final
const resultData = {
    result: isOk ? "Valores Ok" : "Error en cuentas",
    Error: !isOk,
    ErrorMessages: errorMessages,
    missingAccounts: missingAccounts.length > 0 ? missingAccounts : "Ninguna",
    originalData: {
        Invoice_uuid: datForDb.Invoice_uuid || invoiceData.Invoice_uuid || "SIN_UUID",
        ParentDocumentID: invoiceData.ParentDocumentID || "SIN_DOCUMENTO",
        IssueDate: invoiceData.IssueDate || "0000-00-00",

        // Datos de Quien Envia y Recibe
        SenderParty_CompanyID: invoiceData.SenderParty_CompanyID || "SIN_ID",
        SenderParty_RegistrationName: invoiceData.SenderParty_RegistrationName || "SIN_NOMBRE",
        ReceiverParty_CompanyID: invoiceData.ReceiverParty_CompanyID || "SIN_ID",
        ReceiverParty_RegistrationName: invoiceData.ReceiverParty_RegistrationName || "SIN_NOMBRE",

        // Información Económica
        PayableAmount: invoiceData.PayableAmount !== null ? invoiceData.PayableAmount : 0,
        LineExtensionAmount: invoiceData.LineExtensionAmount !== null ? invoiceData.LineExtensionAmount : 0,
        TaxInclusiveAmount: invoiceData.TaxInclusiveAmount !== null ? invoiceData.TaxInclusiveAmount : 0,
        IVA: invoiceData.IVA !== null ? invoiceData.IVA : 0,
        ReteFuente: invoiceData.ReteFuente !== null ? invoiceData.ReteFuente : 0,
        ReteICA: invoiceData.ReteICA !== null ? invoiceData.ReteICA : 0,
        InvoiceDescription: invoiceData.InvoiceDescription || "DESCRIPCIÓN PENDIENTE",
        ExpenseType: invoiceData.ExpenseType || "PENDIENTE",

        // Cuentas Contables
        AccountNum1: datForDb.AccountNum1 || null,
        AccountNum1_Des: datForDb.AccountNum1_Des || null,
        AccountNum1_Val: datForDb.AccountNum1_Val || 0,
        AccountNum1_Deb_Cred: datForDb.AccountNum1_Deb_Cred || null,

        AccountNum2: datForDb.AccountNum2 || null,
        AccountNum2_Des: datForDb.AccountNum2_Des || null,
        AccountNum2_Val: datForDb.AccountNum2_Val || 0,
        AccountNum2_Deb_Cred: datForDb.AccountNum2_Deb_Cred || null,

        AccountNum3: datForDb.AccountNum3 || null,
        AccountNum3_Des: datForDb.AccountNum3_Des || null,
        AccountNum3_Val: datForDb.AccountNum3_Val || 0,
        AccountNum3_Deb_Cred: datForDb.AccountNum3_Deb_Cred || null,

        AccountNum8: datForDb.AccountNum8 || null,
        AccountNum8_Des: datForDb.AccountNum8_Des || null,
        AccountNum8_Val: datForDb.AccountNum8_Val || 0,
        AccountNum8_Deb_Cred: datForDb.AccountNum8_Deb_Cred || null
    },
    Error_log: errorLog
};

// Log para depuración (opcional)
console.log("📌 Resultado de validación:", JSON.stringify(resultData, null, 2));

return [{ json: resultData }];

