function cleanValor(valor) {
    if (typeof valor !== 'string') valor = String(valor);
    valor = valor.trim().replace(/\$/g, '').replace(/\s/g, '');
    if (valor.includes(',')) {
        valor = valor.replace(/\./g, '').replace(',', '.');
    } else if (valor.match(/[.]\d{2}$/)) {
        valor = valor.replace(/,/g, '');
    }
    const num = Number(valor);
    return Math.abs(Math.trunc(num));
}

function formatFechaDDMMYYYY(fecha) {
    if (!fecha || !fecha.match(/^\d{4}-\d{2}-\d{2}$/)) return null;

    const [y, m, d] = fecha.split('-');
    return `${d}/${m}/${y}`;
}

function cleanFecha(fecha) {
    if (fecha === null || fecha === undefined) return null;

    // Si es un n√∫mero de Excel
    if (!isNaN(fecha) && typeof fecha !== 'string') {
        const baseDate = new Date(1900, 0, 1);
        baseDate.setDate(baseDate.getDate() + Number(fecha) - 1); // <- corregido
        return baseDate.toISOString().split('T')[0]; // yyyy-mm-dd
    }

    fecha = String(fecha).trim();

    // Caso: "dd/mm" sin a√±o
    if (fecha.match(/^(\d{1,2})\/(\d{1,2})$/)) {
        let [_, d, m] = fecha.match(/^(\d{1,2})\/(\d{1,2})$/);
        let y = new Date().getFullYear(); // Asume a√±o actual
        return `${y}-${m.padStart(2, '0')}-${d.padStart(2, '0')}`;
    }

    // Caso: "dd/mm/yyyy"
    if (fecha.match(/^(\d{1,2})\/(\d{1,2})\/(\d{2,4})$/)) {
        let [_, d, m, y] = fecha.match(/^(\d{1,2})\/(\d{1,2})\/(\d{2,4})$/);
        if (y.length === 2) y = '20' + y;
        return `${y}-${m.padStart(2, '0')}-${d.padStart(2, '0')}`;
    }

    return fecha;
}

