const MATCH_DAYS = 3;     // Aumentar d√≠as para mayor flexibilidad
const MATCH_TOL = 5000;  // Aumentar tolerancia para manejar diferencias de formato

// Tasas de cambio aproximadas
const USD_TO_COP = 4380;

// Funci√≥n auxiliar para normalizar fechas
function normalizarFecha(fecha, esExtracto = false) {
  if (!fecha) return null;

  // Si es string de fecha completa
  if (typeof fecha === 'string') {
    // Formato DD/MM/YYYY
    if (/^\d{1,2}\/\d{1,2}\/\d{4}$/.test(fecha)) {
      const [dia, mes, ano] = fecha.split('/');
      const fechaCompleta = `${ano}-${mes.padStart(2, '0')}-${dia.padStart(2, '0')}`;
      const fechaObj = new Date(fechaCompleta);
      return isNaN(fechaObj.getTime()) ? null : fechaObj;
    }

    // Formato DD/MM sin a√±o (para extractos)
    if (esExtracto && /^\d{1,2}\/\d{1,2}$/.test(fecha)) {
      const [dia, mes] = fecha.split('/');
      const fechaCompleta = `2024-${mes.padStart(2, '0')}-${dia.padStart(2, '0')}`;
      const fechaObj = new Date(fechaCompleta);
      return isNaN(fechaObj.getTime()) ? null : fechaObj;
    }
  }

  // Para otros formatos (ISO, etc.)
  const fechaObj = new Date(fecha);
  return isNaN(fechaObj.getTime()) ? null : fechaObj;
}

// Funci√≥n auxiliar para normalizar valores num√©ricos - SIMPLIFICADA
function normalizarMonto(monto, moneda = 'COP') {
  if (monto === null || monto === undefined || monto === '') return 0;

  // Si ya es n√∫mero, convertir directamente
  if (typeof monto === 'number') {
    const valorCOP = moneda === 'USD' ? monto * USD_TO_COP : monto;
    return Math.abs(valorCOP);
  }

  // Convertir a string y limpiar
  let numeroStr = monto.toString();

  // Eliminar espacios
  numeroStr = numeroStr.replace(/\s/g, '');

  // Si contiene comas, reemplazar por puntos para decimal
  if (numeroStr.includes(',')) {
    // Caso: 1.234.567,89 -> 1234567.89
    if (numeroStr.includes('.') && numeroStr.includes(',')) {
      numeroStr = numeroStr.replace(/\./g, '').replace(',', '.');
    } else {
      // Caso: 1234,89 -> 1234.89
      numeroStr = numeroStr.replace(',', '.');
    }
  }

  // Eliminar puntos que son separadores de miles
  const partes = numeroStr.split('.');
  if (partes.length > 2) {
    // M√∫ltiples puntos = separadores de miles
    numeroStr = partes.slice(0, -1).join('') + '.' + partes[partes.length - 1];
  } else if (partes.length === 2 && partes[1].length > 2) {
    // Punto como separador de miles
    numeroStr = numeroStr.replace(/\./g, '');
  }

  const numero = parseFloat(numeroStr);

  if (isNaN(numero)) {
    console.warn(`‚ö†Ô∏è  No se pudo parsear el monto: "${monto}" -> "${numeroStr}"`);
    return 0;
  }

  const valorCOP = moneda === 'USD' ? numero * USD_TO_COP : numero;
  return Math.abs(valorCOP);
}

// Funci√≥n auxiliar para comparar empresas/descripciones - SIMPLIFICADA Y M√ÅS FLEXIBLE
function compararEmpresa(factura, extracto) {
  const empresaFac = (factura.empresa || factura.descripcion || '').toLowerCase().trim();
  const descExtracto = (extracto.descripcion || '').toLowerCase().trim();

  // Si alguno est√° vac√≠o, no se puede comparar
  if (!empresaFac || !descExtracto) {
    console.log(`    ‚ùå Uno de los campos est√° vac√≠o`);
    return false;
  }

  // Limpiar y normalizar texto
  const limpiarTexto = (texto) => {
    return texto
      .replace(/[^\w\s]/g, ' ')  // Reemplazar caracteres especiales con espacios
      .replace(/\s+/g, ' ')      // M√∫ltiples espacios -> uno solo
      .trim();
  };

  const facturaNorm = limpiarTexto(empresaFac);
  const extractoNorm = limpiarTexto(descExtracto);

  // 1. Coincidencia exacta (normalizada)
  if (facturaNorm === extractoNorm) {
    console.log(`    ‚úÖ Match exacto`);
    return true;
  }

  // 2. Uno contiene al otro
  if (facturaNorm.includes(extractoNorm) || extractoNorm.includes(facturaNorm)) {
    console.log(`    ‚úÖ Match por contenci√≥n`);
    return true;
  }

  // 3. Mapeo espec√≠fico de t√©rminos conocidos
  const mapeoTerminos = {
    'uber': ['uber', 'rides', 'viaje'],
    'cabify': ['cabify', 'viaje'],
    'spirit': ['spirit', 'airlines'],
    'ceviche': ['ceviche', 'camaron'],
    'centro': ['centro', 'che'],
    'green': ['green', 'office'],
    'step': ['step', 'ahead'],
    'hotel': ['hotel', 'estelar'],
    'clases': ['clases', 'personalizadas']
  };

  for (const [termino, variantes] of Object.entries(mapeoTerminos)) {
    const tieneFactura = variantes.some(v => facturaNorm.includes(v));
    const tieneExtracto = variantes.some(v => extractoNorm.includes(v));

    if (tieneFactura && tieneExtracto) {
      console.log(`    ‚úÖ Match por mapeo de t√©rmino: "${termino}"`);
      return true;
    }
  }

  // 4. Coincidencia por palabras individuales
  const palabrasFactura = facturaNorm.split(/\s+/).filter(p => p.length > 2);
  const palabrasExtracto = extractoNorm.split(/\s+/).filter(p => p.length > 2);

  let coincidencias = 0;
  for (const palabraFac of palabrasFactura) {
    for (const palabraExt of palabrasExtracto) {
      if (palabraFac === palabraExt || 
          palabraFac.includes(palabraExt) || 
          palabraExt.includes(palabraFac)) {
        coincidencias++;
        console.log(`    üéØ Coincidencia de palabra: "${palabraFac}" <-> "${palabraExt}"`);
        break;
      }
    }
  }

  // Si hay al menos 1 coincidencia significativa, es v√°lido
  if (coincidencias > 0) {
    console.log(`    ‚úÖ Match por coincidencia de palabras (${coincidencias})`);
    return true;
  }

  // 5. Como √∫ltimo recurso, verificar referencia
  const referenciaFac = (factura.referencia || '').toString().toLowerCase();
  if (referenciaFac && extractoNorm.includes(referenciaFac)) {
    console.log(`    ‚úÖ Match por referencia: "${referenciaFac}"`);
    return true;
  }

  console.log(`    ‚ùå Sin coincidencia`);
  return false;
}

// Funci√≥n para procesar una lista de facturas
function procesarFacturas(facturas, extractos, usadosExtractos, tipoFactura) {
  const conciliados = [];
  const pendientes = [];

  for (const factura of facturas) {
    let mejorMatch = null;
    let menorDiferencia = Infinity;
    let mejorIndice = -1;

    // Normalizar datos de la factura
    const fechaFac = normalizarFecha(factura.fecha_fact);
    const montoFac = normalizarMonto(factura.monto_fact, factura.moneda);

    if (!fechaFac) {
      console.warn(`‚ö†Ô∏è  ${tipoFactura} con fecha inv√°lida: ${JSON.stringify(factura)}`);
      continue;
    }

    // Buscar candidatos sin usar el filtro de empresa primero
    const candidatos = [];

    for (let i = 0; i < extractos.length; i++) {
      if (usadosExtractos.has(i)) continue;

      const extracto = extractos[i];
      const fechaExt = normalizarFecha(extracto.fecha_fact, true);
      const montoExt = normalizarMonto(extracto.monto_fact, extracto.moneda || 'COP');

      if (!fechaExt) {
        console.warn(`‚ö†Ô∏è  Extracto ${i} con fecha inv√°lida: ${extracto.fecha_fact}`);
        continue;
      }

      // Calcular diferencias
      const diffDias = Math.abs((fechaFac - fechaExt) / (1000 * 60 * 60 * 24));
      const diffValor = Math.abs(montoFac - montoExt);
      const empresaOk = compararEmpresa(factura, extracto);

      // Criterios m√°s flexibles
      const cumpleDias = diffDias <= MATCH_DAYS;
      const cumpleValor = diffValor <= MATCH_TOL;

      if (cumpleDias && cumpleValor && empresaOk) {
        const score = diffDias + (diffValor / 10000);

        candidatos.push({
          extracto,
          indice: i,
          score,
          diffDias,
          diffValor
        });
      } else {
        console.log(`  ‚ùå No cumple criterios:`);
        console.log(`    - D√≠as: ${cumpleDias} (${diffDias.toFixed(1)} <= ${MATCH_DAYS})`);
        console.log(`    - Valor: ${cumpleValor} (${diffValor} <= ${MATCH_TOL})`);
        console.log(`    - Empresa: ${empresaOk}`);
      }
    }

    // Elegir el mejor candidato
    if (candidatos.length > 0) {
      const mejor = candidatos.reduce((mejor, actual) => 
        actual.score < mejor.score ? actual : mejor
      );


      usadosExtractos.add(mejor.indice);
      conciliados.push({
        factura: { ...factura, tipo: tipoFactura },
        extracto: mejor.extracto,
        estado: "Conciliado OK",
        score: mejor.score,
        tipoFactura: tipoFactura
      });
    } else {
      pendientes.push({
        factura: { ...factura, tipo: tipoFactura },
        estado: "Sin coincidencia en extractos",
        tipoFactura: tipoFactura
      });
    }
  }

  return { conciliados, pendientes };
}

// Obtener los datos de entrada
const facturas = items[0].json.facturas || [];
const ubercabify = items[0].json.ubercabify || [];
const extractos = items[0].json.extractos || [];

// Validaci√≥n de datos de entrada
if (!Array.isArray(facturas)) {
  console.error('‚ùå Error: facturas no es un array v√°lido');
  return [{ json: { error: 'Facturas inv√°lidas' } }];
}

if (!Array.isArray(ubercabify)) {
  console.error('‚ùå Error: ubercabify no es un array v√°lido');
  return [{ json: { error: 'UberCabify inv√°lidas' } }];
}

if (!Array.isArray(extractos)) {
  console.error('‚ùå Error: extractos no es un array v√°lido');
  return [{ json: { error: 'Extractos inv√°lidos' } }];
}

// Inicializar arrays de resultados
let todosLosConciliados = [];
let todasLasPendientes = [];
const usadosExtractos = new Set();

// Procesar facturas regulares
const resultadosFacturas = procesarFacturas(facturas, extractos, usadosExtractos, 'factura');
todosLosConciliados = todosLosConciliados.concat(resultadosFacturas.conciliados);
todasLasPendientes = todasLasPendientes.concat(resultadosFacturas.pendientes);

// Procesar facturas UberCabify
const resultadosUberCabify = procesarFacturas(ubercabify, extractos, usadosExtractos, 'ubercabify');
todosLosConciliados = todosLosConciliados.concat(resultadosUberCabify.conciliados);
todasLasPendientes = todasLasPendientes.concat(resultadosUberCabify.pendientes);

// Extractos sin usar
const pendientesExtracto = extractos
  .map((extracto, i) => ({ 
    extracto, 
    estado: "Sin coincidencia en facturas",
    indice: i 
  }))
  .filter((item) => !usadosExtractos.has(item.indice))
  .map(item => ({ extracto: item.extracto, estado: item.estado }));

return [
  { json: { conciliados: todosLosConciliados, total: todosLosConciliados.length } },
  { json: { pendientesFactura: todasLasPendientes, total: todasLasPendientes.length } },
  { json: { pendientesExtracto, total: pendientesExtracto.length } }
];
