        "jsCode": "function cleanValor(valor) {
    if (typeof valor !== 'string') valor = String(valor);
    valor = valor.trim().replace(/\$/g, '').replace(/\s/g, '');
    if (valor.includes(',')) {
        valor = valor.replace(/\./g, '').replace(',', '.');
    } else if (valor.match(/[.]\d{2}$/)) {
        valor = valor.replace(/,/g, '');
    }
    const num = Number(valor);
    return Math.abs(Math.trunc(num));
}

function cleanFecha(fecha) {
    if (fecha === null || fecha === undefined) return null;

    // Si es un n√∫mero de Excel (ej: 45394)
    if (!isNaN(fecha) && typeof fecha !== 'string') {
        const baseDate = new Date(1900, 0, 1);
        baseDate.setDate(baseDate.getDate() + Number(fecha) - 2); // <- corregido aqu√≠
        const day = baseDate.getDate().toString().padStart(2, '0');
        const month = (baseDate.getMonth() + 1).toString().padStart(2, '0');
        const year = baseDate.getFullYear();
        return `${day}/${month}/${year}`;
    }

    // Si es string (tipo "4/12/2024")
    fecha = String(fecha).trim();
    if (fecha.match(/^(\d{1,2})\/(\d{1,2})\/(\d{2,4})$/)) {
        let [_, d, m, y] = fecha.match(/^(\d{1,2})\/(\d{1,2})\/(\d{2,4})$/);
        if (y.length === 2) y = '20' + y;
        return `${d.padStart(2, '0')}/${m.padStart(2, '0')}/${y}`;
    }

    return fecha;
}

let facturas = items.map(e => {
    return {
        descripcion: e.json['Descripci√≥n'] || '',
        empresa: e.json['Empresa emisora'] || '',
        referencia: e.json['N√∫mero de comprobante o referencia'] || '',
        fecha_fact: cleanFecha(e.json['Fecha de pago o compra']),
        monto_fact: cleanValor(e.json['Monto total cobrado']),
        moneda: e.json['Moneda'] || 'COP',
        URL: e.json['Url factura']
    };
});

return [
    {
        json: {
            facturas: facturas
        }
    }
