      "parameters": {
        "jsCode": "const entrada = $input.item.json;

function parseFecha(fechaStr) {
  if (!fechaStr) return null;
  const [day, month, year] = fechaStr.split('/');
  return new Date(`${year}-${month}-${day}`);
}

function fechasCercanas(fecha1, fecha2, margenDias = 3) {
  if (!fecha1 || !fecha2) return false;
  const diff = Math.abs(fecha1 - fecha2);
  return diff <= margenDias * 86400000;
}

function normalizarTexto(texto) {
  return texto?.toLowerCase()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/[^a-z0-9]/g, ' ')
    .replace(/\s+/g, ' ')
    .trim() || '';
}

const extractos = entrada.extractos.filter(e => e.monto_fact > 0);
const facturas = entrada.facturas.filter(f => f.monto_fact > 0);

const conciliados = [];
const facturasPendientes = [];
const extractosSinConciliar = [];

const usadosExtractos = new Set();

for (const factura of facturas) {
  const fechaFactura = parseFecha(factura.fecha_fact);
  const montoFactura = factura.monto_fact;
  const descFactura = normalizarTexto(factura.descripcion);
  const empresaFactura = normalizarTexto(factura.empresa);

  let matchIndex = -1;

  for (let i = 0; i < extractos.length; i++) {
    if (usadosExtractos.has(i)) continue;

    const extr = extractos[i];
    const fechaExtracto = parseFecha(extr.fecha_fact);
    const montoExtracto = extr.monto_fact;
    const descExtracto = normalizarTexto(extr.descripcion);

    const montoIgual = montoFactura === montoExtracto;
    const fechaSimilar = fechasCercanas(fechaFactura, fechaExtracto);
    const descripcionRelacionada =
      (descExtracto.includes('uber') && empresaFactura.includes('uber')) ||
      (descExtracto.includes('cabify') && empresaFactura.includes('cabify')) ||
      descExtracto.includes(normalizarTexto(factura.empresa));

    if (montoIgual && fechaSimilar && descripcionRelacionada) {
      matchIndex = i;
      break;
    }
  }

  if (matchIndex >= 0) {
    usadosExtractos.add(matchIndex);
    conciliados.push({
      factura,
      extracto: extractos[matchIndex]
    });
  } else {
    facturasPendientes.push(factura);
  }
}

// Cargar extractos no usados
extractos.forEach((e, i) => {
  if (!usadosExtractos.has(i)) extractosSinConciliar.push(e);
});

return [
  { json: { conciliados } },
  { json: { facturasPendientes } },
  { json: { extractosSinConciliar } }
];
