      "parameters": {
        "jsCode": "// Get the raw input data from the workflow\nconst inputText = $json.content[0].text;\n\n// Fix invalid JSON format by replacing single quotes with double quotes\nconst correctedJson = inputText.replace(/'/g, '\"');\n\ntry {\n    // Parse the corrected JSON string into an object\n    const extractedData = JSON.parse(correctedJson);\n\n    // Return the structured JSON object\n    return [{ json: extractedData }];\n} catch (error) {\n    // If parsing fails, return an error message\n    return [{ json: { error: \"Invalid JSON format\", details: error.message } }];\n}"
